import GridScan from './GridScan';

<div style={{ width: '100%', height: '600px', position: 'relative' }}>
 <GridScan
 sensitivity={0.55}
 lineThickness={1}
 linesColor="#392e4e"
 gridScale={0.1}
 scanColor="#FF9FFC"
 scanOpacity={0.4}
 enablePost
 bloomIntensity={0.6}
 chromaticAberration={0.002}
