 <video ref={videoRef} muted playsInline autoPlay className="w-full h-full object-cover -scale-x-100" />
 <div className="absolute left-2 top-2 px-[6px] py-[2px] bg-black/50 rounded-[6px] backdrop-blur-[4px]">
