+ const beta = e.beta ?? 0;
+ const nx = THREE.MathUtils.clamp(gamma / 45, -1, 1);
+ const ny = THREE.MathUtils.clamp(-beta / 30, -1, 1);
+ lookTarget.current.set(nx, ny);
+ tiltTarget.current = THREE.MathUtils.degToRad(gamma) * 0.4;
