+ let canceled = false;
+ const load = async () => {
+ try {
+ if (!canceled) setModelsReady(true);
+ } catch {
+ if (!canceled) setModelsReady(false);
+ }
+ };
+ load();
+ return () => {
+ canceled = true;
+ };
